import React from 'react';
import { connect } from 'react-redux';
import { submitForm } from './actions';
import './App.css';

class App extends React.Component() {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);
    this.state = {
      name: '',
      email: '',
      cpf: '',
      adress: '',
      city: '',
/*       state: '',
      typePlace: '',
      resume: '',
      job: '',
      jobDescription: '', */
    }
  }

  handleChange({target}) {
    this.setState({
      [target.name]: target.value });
  };

  render() {
    return (
      <div className="App">
        <fieldset>
          <input type="text" name='name' maxlength='40' required
            onChange={this.handleChange} />
          <input type="text" name='email' maxlength='50' required
            onChange={this.handleChange} />
          <input type="text" name='cpf' maxlength='11'required
            onChange={this.handleChange} />
          <input type="text" name='adress' maxlength='200' required
            onChange={this.handleChange} />
          <input type="text" name='city' maxlength='28' required
            onChange={this.handleChange} />
        </fieldset>
        <button type="button" onClick={ submitForm(this.state) }>Submit</button>
        <button type="button" onClick={ submitForm(this.state) }>Clean</button>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  submitForm: (payload) => dispatch(submitForm(payload))
});

export default connect(null, mapDispatchToProps)(App);
