import React from 'react';
import { connect } from 'react-redux';
import { submitForm } from './actions';
import { cleanForm } from './actions';
import './App.css';

class App extends React.Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);
    this.state = {
      name: '',
      email: '',
      cpf: '',
      adress: '',
      city: '',
    }
  }

  handleChange({target}) {
    this.setState({
      [target.name]: target.value });
  };

  render() {
    return (
      <div className="App">
        <fieldset>
          <label htmlFor="name">Nome:
            <input type="text" name='name' maxlength='40' required
              onChange={this.handleChange} />
          </label>
          <label htmlFor="name">E-mail:
            <input type="text" name='email' maxlength='50' required
              onChange={this.handleChange} />
          </label>
          <label htmlFor="name">Cpf:
            <input type="text" name='cpf' maxlength='11'required
              onChange={this.handleChange} />
          </label>
          <label htmlFor="name">Endere√ßo:
            <input type="text" name='adress' maxlength='200' required
              onChange={this.handleChange} />
          </label>
          <label htmlFor="name">Cidade:
            <input type="text" name='city' maxlength='28' required
              onChange={this.handleChange} />
          </label>
        </fieldset>
        <button type="button" onClick={ submitForm(this.state) }>Submeter</button>
        <button type="button" onClick={ cleanForm() }>Limpar</button>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  submitForm: (payload) => dispatch(submitForm(payload)),
  cleanForm: () => dispatch(cleanForm())
});

export default connect(null, mapDispatchToProps)(App);
