const INITIAL_STATE = {
  name: '',
  email: '',
  cpf: '',
  adress: '',
  city: '',
}

const formReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "SUBMIT_FORM":
      return {
        name: action.payload.name,
        email: action.payload.email,
        cpf: action.payload.cpf,
        adress: action.payload.adress,
        city: action.payload.city,
      };
    default:
      return state;
  }
}

export default formReducer;
