// Quais reducers usar ?
// reducers agrupam coisas similares
// varia com a aplicação - não tem certo e errado

// Definir um estado para cada action
// ex: ADD_PRODUCT, REMOVE-PRODUCT...
import { combineReducers } from 'redux';
import inputsReducer from './inputsReducer';

const rootReducer = combineReducers({
  inputsReducer,
});

export default rootReducer;
