import React from 'react';
import { connect } from 'react-redux';
import { submitForm } from './actions';
import { cleanForm } from './actions';
import './App.css';

class App extends React.Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);
    this.cleanForm = this.cleanForm.bind(this);

    this.state = {
      name: '',
      email: '',
      cpf: '',
      adress: '',
      city: '',
    }
  }

  handleChange({target}) {
    this.setState({
      [target.name]: target.value });
  };

  cleanForm() {
    this.setState({
      name: '',
      email: '',
      cpf: '',
      adress: '',
      city: '',
    });
  }

  render() {
    const { name, email, cpf, adress, city } = this.state;

    return (
      <div className="App">
        <form>
          <fieldset>
            <label htmlFor="name">Nome:
              <input type="text" name='name' maxlength='40' required value={ name }
                onChange={this.handleChange} />
            </label>
            <label htmlFor="name">E-mail:
              <input type="text" name='email' maxlength='50' required value={ email }
                onChange={this.handleChange} />
            </label>
            <label htmlFor="name">Cpf:
              <input type="text" name='cpf' maxlength='11'required value={ cpf }
                onChange={this.handleChange} />
            </label>
            <label htmlFor="name">Endere√ßo:
              <input type="text" name='adress' maxlength='200' required value={ adress }
                onChange={this.handleChange} />
            </label>
            <label htmlFor="name">Cidade:
              <input type="text" name='city' maxlength='28' required value={ city }
                onChange={this.handleChange} />
            </label>
          </fieldset>
          <button type="submit" onClick={ submitForm(this.state) }>Submeter</button>
          <button type="button" onClick={ cleanForm }>Limpar</button>
        </form>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  submitForm: (payload) => dispatch(submitForm(payload)),
 // cleanForm: () => dispatch(cleanForm())
});

export default connect(null, mapDispatchToProps)(App);
